package pkg1;

//50. Pow(x, n)

public class PowXN {
	public static double myPow(double x, int n) {
        
        double result = Math.pow(x, n);
        
       return result; 
        
        
    }
    

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    
	    double x=2.0000;
	        int n = 10;
	        
	        System.out.println(myPow(x,n));
	}

}
